var originalText=[],idTracker=[],running=!0;async function heroType(e){let t=e.split("");originalText=t;try{for(char of t)await autoType(char);updateText(),resetText()}catch{console.log("Hero Typing Stopped")}}function autoType(e){let t=document.getElementById("hero-banner");return new Promise((n,r)=>{let o=setTimeout(function(){t.innerText+=e,clearInterval(o),n(!0)}.bind(t),Math.floor(400*Math.random()))})}function updateText(){let e=setInterval(function(){if(randomChoice()){let e=document.getElementById("hero-banner").innerText.split(""),t=Math.floor(Math.random()*e.length)-1,n=String.fromCharCode(Math.floor(93*Math.random())+33);e[t];e[t]=n,setText(e.join("").trim())}running||clearInterval(e)},200);trackThisId("interval",e)}function setText(e){document.getElementById("hero-banner").innerText=e}function resetText(e,t){let n=document.getElementById("hero-banner").innerText.split(""),r=[],o=setInterval(()=>{for(let e=0;e<n.length;e++)randomChoice()?r[e]=originalText[e]:r[e]=n[e];setText(r.join("")),running||clearInterval(o)},400);trackThisId("interval",o)}function trackThisId(e,t){idTracker.push({type:e,id:t})}function heroTypeStop(){running=!1}function randomChoice(){return Math.floor(40*Math.random())>=30}